import React, { useState, useCallback, useEffect, useRef } from 'react';
import { parseBlob } from 'music-metadata-browser';
import { extractM4BChapters } from '../utils/m4bParser';
import FileUploader from '../Components/audiobook/FileUploader';
import AudiobookPlayer from '../Components/audiobook/AudiobookPlayer';
import Library from '../Components/audiobook/Library';
import { saveFile, getFile, updateLibraryEntry } from '../utils/idbFileStore';

const STORAGE_KEY = 'audiobook_player_state';

export default function AudiobookPage() {
    const [currentFileId, setCurrentFileId] = useState(null);
    const [audioFile, setAudioFile] = useState(null);
    const [chapters, setChapters] = useState([]);
    const [bookInfo, setBookInfo] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [savedState, setSavedState] = useState(null);
    
    // Use ref to always have access to the latest currentFileId without recreating callbacks
    const currentFileIdRef = useRef(null);
    
    // Keep ref in sync with state
    useEffect(() => {
        currentFileIdRef.current = currentFileId;
    }, [currentFileId]);

    // Load saved state on mount
    useEffect(() => {
        const saved = localStorage.getItem(STORAGE_KEY);
        if (saved) {
            try {
                const state = JSON.parse(saved);
                setSavedState(state);
            } catch (error) {
                console.error('Error loading saved state:', error);
            }
        }
    }, []);
    
    const parseM4BChapters = useCallback(async (file) => {
        try {
            const extractedChapters = [];
            const info = {
                title: null,
                artist: null,
                album: null,
                cover: null
            };
            
            // First, try to extract chapters using binary parsing (like VLC)
            console.log('Attempting to extract chapters from M4B file...');
            const binaryChapters = await extractM4BChapters(file);
            
            if (binaryChapters.length > 0) {
                console.log(`Successfully extracted ${binaryChapters.length} chapters from binary parsing`);
                extractedChapters.push(...binaryChapters);
            }
            
            // Also get metadata using music-metadata for title, artist, cover, etc.
            let metadata;
            try {
                metadata = await parseBlob(file, { 
                    duration: true,
                    skipCovers: false,
                    native: true
                });
                
                // Extract basic info
                info.title = metadata.common.title || file.name.replace(/\.[^/.]+$/, '');
                info.artist = metadata.common.artist || metadata.common.albumartist || null;
                info.album = metadata.common.album || null;
                
                // Extract cover art
                if (metadata.common.picture && metadata.common.picture.length > 0) {
                    const picture = metadata.common.picture[0];
                    const blob = new Blob([picture.data], { type: picture.format });
                    info.cover = URL.createObjectURL(blob);
                }
                
                const duration = metadata.format.duration || 0;
                
                // If binary parsing didn't find chapters, try metadata
                if (extractedChapters.length === 0) {
                    console.log('No chapters from binary parsing, trying music-metadata...');
                    
                    if (metadata.native) {
                        // Check all native keys for chapter data
                        for (const key in metadata.native) {
                            const items = metadata.native[key];
                            
                            if (Array.isArray(items)) {
                                const chapterItems = items.filter(tag => 
                                    tag.id === 'chpl' || 
                                    tag.id === 'chapters' ||
                                    (tag.mean && tag.mean.includes('chapter'))
                                );
                                
                                if (chapterItems.length > 0) {
                                    console.log(`Found chapter items in native.${key}`);
                                    
                                    for (const chplData of chapterItems) {
                                        let chapters = [];
                                        if (Array.isArray(chplData.value)) {
                                            chapters = chplData.value;
                                        } else if (chplData.value && typeof chplData.value === 'object') {
                                            chapters = Array.isArray(chplData.value.chapters) 
                                                ? chplData.value.chapters 
                                                : Object.values(chplData.value);
                                        }
                                        
                                        for (const chapter of chapters) {
                                            if (chapter && (chapter.title || chapter.startTime !== undefined)) {
                                                extractedChapters.push({
                                                    title: chapter.title || chapter.name || `Chapter ${extractedChapters.length + 1}`,
                                                    startTime: (chapter.startTime || chapter.start || 0) / 1000,
                                                    duration: null
                                                });
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
                
                // Calculate chapter durations if chapters were found
                if (extractedChapters.length > 0) {
                    extractedChapters.sort((a, b) => a.startTime - b.startTime);
                    
                    extractedChapters.forEach((chapter, index) => {
                        const nextChapter = extractedChapters[index + 1];
                        chapter.duration = nextChapter 
                            ? nextChapter.startTime - chapter.startTime
                            : duration - chapter.startTime;
                    });
                    
                    console.log(`Successfully extracted ${extractedChapters.length} chapters`);
                    return { chapters: extractedChapters, info };
                }
                
                // Fallback: create time-based chapters if no chapters found
                if (duration > 0) {
                    const chapterDuration = 600; // 10 minute segments
                    const numChapters = Math.ceil(duration / chapterDuration);
                    
                    for (let i = 0; i < numChapters; i++) {
                        const startTime = i * chapterDuration;
                        extractedChapters.push({
                            title: `Part ${i + 1}`,
                            startTime: startTime,
                            duration: Math.min(chapterDuration, duration - startTime)
                        });
                    }
                    
                    console.log(`No chapters found. Created ${extractedChapters.length} time-based segments`);
                }
                
            } catch (metadataError) {
                console.error('Error parsing metadata:', metadataError);
                info.title = file.name.replace(/\.[^/.]+$/, '');
            }
            
            return { chapters: extractedChapters, info };
            
        } catch (error) {
            console.error('Error parsing M4B file:', error);
            return { 
                chapters: [], 
                info: { 
                    title: file.name.replace(/\.[^/.]+$/, ''), 
                    artist: null, 
                    album: null,
                    cover: null 
                } 
            };
        }
    }, []);

    const handleFileSelect = useCallback(async (file) => {
        setIsLoading(true);
        
        try {
            const { chapters: extractedChapters, info } = await parseM4BChapters(file);
            setChapters(extractedChapters);
            setBookInfo(info);
            setAudioFile(file);
            
            // Save file to library with metadata
            try {
                const fileId = await saveFile(file, {
                    title: info.title || file.name.replace(/\.[^/.]+$/, ''),
                    artist: info.artist || 'Unknown Artist',
                    cover: info.cover || null,
                    duration: info.duration || 0,
                });
                setCurrentFileId(fileId);
            } catch (idbErr) {
                console.warn('Failed to save file to library:', idbErr);
            }
        } catch (error) {
            console.error('Error parsing audiobook:', error);
            setAudioFile(file);
            setChapters([]);
            setBookInfo({ title: file.name.replace(/\.[^/.]+$/, '') });
        } finally {
            setIsLoading(false);
        }
    }, [parseM4BChapters]);

    const handleClose = useCallback(() => {
        setAudioFile(null);
        setChapters([]);
        setBookInfo(null);
        setCurrentFileId(null);
        setSavedState(null);
        // Clear saved state
        try {
            localStorage.removeItem(STORAGE_KEY);
        } catch (err) {
            // ignore
        }
    }, []);

    const handleLibrarySelect = useCallback(async (fileId, libraryEntry) => {
        setIsLoading(true);
        try {
            // Load file from IndexedDB
            const file = await getFile(fileId);
            if (!file) {
                throw new Error('File not found in storage');
            }

            console.log('Loading book from library:', libraryEntry.title, 'playback position:', libraryEntry.playbackPosition);

            // Parse chapters
            const { chapters: extractedChapters, info } = await parseM4BChapters(file);
            setChapters(extractedChapters);
            setBookInfo({
                ...info,
                title: libraryEntry.title || info.title,
                artist: libraryEntry.artist || info.artist,
                cover: libraryEntry.cover || info.cover,
            });
            
            // Force audio remount by creating new file object with timestamp
            // This ensures clean state when switching between library items
            const newFile = new File([file], file.name, { type: file.type });
            setAudioFile(newFile);
            setCurrentFileId(fileId);
            currentFileIdRef.current = fileId;

            // Load saved playback position (ensure it's a valid number)
            const playbackPosition = typeof libraryEntry.playbackPosition === 'number' 
                ? libraryEntry.playbackPosition 
                : 0;
            
            console.log('Setting saved state with playbackPosition:', playbackPosition);
            setSavedState({
                playbackPosition: playbackPosition,
                currentChapterIndex: 0,
                volume: libraryEntry.volume !== undefined ? libraryEntry.volume : 1,
                speed: libraryEntry.speed !== undefined ? libraryEntry.speed : 1,
            });
        } catch (error) {
            console.error('Error loading file from library:', error);
        } finally {
            setIsLoading(false);
        }
    }, [parseM4BChapters]);

    const handleSaveState = useCallback(async (state) => {
        localStorage.setItem(STORAGE_KEY, JSON.stringify(state));
        
        // Also update library entry with playback position using ref to avoid stale closures
        const fileId = currentFileIdRef.current;
        if (fileId) {
            try {
                console.log('Saving playback state:', state.playbackPosition, 'for file:', fileId);
                await updateLibraryEntry(fileId, {
                    playbackPosition: state.playbackPosition || 0,
                    volume: state.volume !== undefined ? state.volume : 1,
                    speed: state.speed !== undefined ? state.speed : 1,
                    lastPlayed: new Date().toISOString(),
                });
            } catch (err) {
                console.warn('Failed to update playback state in library:', err);
            }
        }
    }, []); // Empty dependency array - callback is stable, uses ref for currentFileId

    return (
        <div className="min-h-screen bg-gradient-to-b from-slate-950 via-slate-900 to-slate-950">
            {!audioFile ? (
                <>
                    <FileUploader 
                        onFileSelect={handleFileSelect} 
                        isLoading={isLoading}
                    />
                    <div className="px-6 py-8">
                        <h2 className="text-2xl font-bold text-white mb-6">Your Library</h2>
                        <Library 
                            onSelectFile={handleLibrarySelect}
                            onLoadingChange={setIsLoading}
                        />
                    </div>
                </>
            ) : (
                <div className="h-screen">
                    <AudiobookPlayer
                        file={audioFile}
                        chapters={chapters}
                        bookInfo={bookInfo}
                        onClose={handleClose}
                        savedState={savedState}
                        onSaveState={handleSaveState}
                    />
                </div>
            )}
        </div>
    );
}
